/*
 * Returns a json object with query test results with which you can compare with during tests.
 * Files are generated by /results/gen_testresults.js
 *
**/

function Result(routine, type, method) {

    var path = "./results/res." +routine+ "." +type;                                        // the common part of path
    path = type==='raw' ? path+ ".json" : path+ "." +method.replace('Merge', '')+ ".json";  // If not raw, add method to file name

    return require(path);                                                                   // Return json object
}

module.exports = Result;